window.addEventListener('load', e => {

    const form = document.querySelector('form');

    form.addEventListener('submit', es => {

        if (form.checkValidity() === false) {

            es.preventDefault();
            es.stopPropagation();

        }

        form.classList.add('was-validated');
    });
});